  mutate(share_decile = weight)

table.income %>% 
  mutate(share_rest = 1 - weight) -> table.income

table.income %>% mutate(
  prop_vote = table.income.pervote$prop[table.income.pervote$votendp == 1]
) %>% gt() %>% 
  gt_theme_dark()
